import React, { useState, useEffect } from 'react';
import './css/App.css';
import axios from 'axios';
import './css/bootstrap.min.css'


function App() {

  const API_URL = "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false";
  const [coins, setCoins] = useState([]);
  useEffect(() => {
    axios.get(API_URL).then(response => {
      setCoins(response.data);
      console.log(response.data);
    })
  }, [])

  return (
    <div className="main-page">
    <input className="float-right mr-2 mt-3" type="text" placeholder="Search crypto..."></input>


    {/* Coins */}
    <table className="table table-dark">
      <thead className="table-header__fontsize">
        <tr>
          <th scope="col">#</th>
          <th scope="col">Name</th>
          <th scope="col">Price</th>
          <th scope="col">24h %</th>
          {/* <th scope="col">7d %</th> */}
          <th scope="col">Mkt. Cap</th>
          <th scope="col">Volume(24h)</th>
          <th scope="col">Circulating Supply</th>
        </tr>
      </thead>
      <tbody className="table-body__fontsize">
        {coins.map((coin) => {
          return(
            <tr>
              <th scope="row">{coin.market_cap_rank}</th>
              <td><img className="mr-2" src={coin.image} style={{height: '25px', width: '25px'}} />{coin.name}</td>
              <td>${coin.current_price.toLocaleString()}</td>
              <td>{coin.price_change_percentage_24h.toFixed(3)}%</td>
              <td>${coin.market_cap.toLocaleString()}</td>
              <td>{coin.total_volume.toLocaleString()}</td>
              <td>{coin.circulating_supply.toLocaleString()} {coin.symbol.toUpperCase()}</td>
            </tr>
          );
        })}
      </tbody>
    </table>
    </div>
  );
}

export default App;
